import logo from './logo.svg';
import React, { useState , useEffect } from 'react';
import './App.css'; 


function App() {

  const [count, setCount] = useState(1);

  const handleCountUpdate = () => {
    setCount(count + 1);
  }
  //1. 랜더링 될때마다 매번 실행됨
  useEffect(()=>{
    console.log("hello");
  })

  return (  
    <div className="App">
      <button onClick={handleCountUpdate}>Update</button>
      <span>count : { count }</span>
    </div>
  );
}
export default App;
